import './bannersStyle.less'
function Banner () {
    // 获取小圆点
    var dotList = document.getElementsByClassName("dot");
    // 获取图片
    var picList = document.getElementsByClassName("pic");
    // 获取模糊背景
    var bg = document.getElementsByClassName("banner");
    // 获取左按钮
    var btnLeft = document.getElementsByClassName("left-btn");
    // 获取右按钮
    var btnRight = document.getElementsByClassName("right-btn");



    // 全局变量，索引
    var index = 0, time;


    // 清除active、up样式
    var clearUp = function () {
        for (var i = 0; i < dotList.length; i++) {
            dotList[i].className = "dot";
            picList[i].className = "pic";
        }
    }
    return false
    // 点击小圆点后相应图片和小圆点的样式改变，同时模糊的背景也改变
    var goDot = function () {
        dotList[index].className = "dot active";
        picList[index].className = "pic up";
        bg[0].style.background = "url(../images/" + index + ".jpeg)";
    }

    // 点击小圆点触发跳转事件
    for (var i = 0; i < dotList.length; i++) {
        dotList[i].addEventListener("click", function () {
            var dotIndex = this.getAttribute("data");
            index = dotIndex;
            clearUp();
            goDot();
            time = 0;
        })
    }


    // 点击左按钮跳转上一张图片
    var goLast = function () {
        if (index > 0) {
            index--;
        } else {
            index = 9;
        }
        clearUp()
        dotList[index].className = "dot active";
        picList[index].className = "pic up";
        bg[0].style.background = "url(../images/" + index + ".jpeg)";
    }

    // 点击右按钮跳转下一张图片
    var goNext = function () {
        if (index < 9) {
            index++;
        } else {
            index = 0;
        }
        clearUp();
        dotList[index].className = "dot active";
        picList[index].className = "pic up";
        bg[0].style.background = "url(../images/" + index + ".jpeg)";
    }

    // 点击左按钮触发跳转事件
    btnLeft[0].addEventListener("click", function () {
        goLast();
        time = 0;
    })

    // 点击右按钮触发跳转事件
    btnRight[0].addEventListener("click", function () {
        goNext();
        time = 0;
    })


    // 自动轮播，每5秒跳转到下一张图片
    setInterval(function () {
        time++;
        if (time == 25) {
            goNext();
            time = 0;
        }
    }, 200)

    return (
        <div className="banner">
            <div className="content">
                <div className="left">
                    <ul className="piclist">
                        <li><a href="#" className="pic"><img src="http://p1.music.126.net/d6QH1DzhJEoMWSE2-TQvyw==/109951169441597763.jpg?imageView&quality=89"></img></a></li>
                        <li><a href="#" className="pic"><img src="http://p1.music.126.net/Iv7bVl_3bXamonu5LFWKgw==/109951169441583934.jpg?imageView&quality=89"></img></a></li>
                        <li><a href="#" className="pic up"><img src="http://p1.music.126.net/wKdPp1Pdy6ueH9lDeN5HCg==/109951169443577352.jpg?imageView&quality=89"></img></a></li>
                        <li><a href="#" className="pic"><img src="http://p1.music.126.net/Iv7bVl_3bXamonu5LFWKgw==/109951169441583934.jpg?imageView&quality=89"></img></a></li>
                    </ul>
                    <ul className="dotlist">
                        <li><a href="#" className="dot active" data="0"></a></li>
                        <li><a href="#" className="dot" data="1"></a></li>
                        <li><a href="#" className="dot" data="2"></a></li>
                        <li><a href="#" className="dot" data="3"></a></li>
                        <li><a href="#" className="dot" data="4"></a></li>
                        <li><a href="#" className="dot" data="5"></a></li>
                        <li><a href="#" className="dot" data="6"></a></li>
                        <li><a href="#" className="dot" data="7"></a></li>
                        <li><a href="#" className="dot" data="8"></a></li>
                        <li><a href="#" className="dot" data="9"></a></li>
                    </ul>
                </div>
                <div className="right">
                    <a href="#">下载客户端</a>
                    <p>PC 安卓 iPhone WP iPad Mac 六大客户端</p>
                </div>
                <a href="#" className="button left-btn"></a>
                <a href="#" className="button right-btn"></a>
            </div>
        </div>
    )
}
export default Banner
