import React from "react"
import Top from './components/Layout/Top'
import Foot from './components/Layout/Foot'
import PlayerBar from './components/Layout/FootPlay/PlayerBar/PlayerBar'
import Banner from './components/FindMusic/Banner'
import routes from './reouter/index';
import { useRoutes, Outlet } from "react-router-dom"
import './App.less'
import { RequestGet } from './request/api/login'

import { connect } from 'react-redux'

function App (props) {
  const routerView = useRoutes(routes)
  return (
    <div className="app">
      {/**布局 */}
      {/* <Top></Top>
      <Foot></Foot>
      <Banner></Banner>

      <PlayerBar></PlayerBar> */}
      <div className="sssssssss">
        {props.str}
      </div>
      {routerView}{/**路由出口 */}
    </div>
  )
}

/**state映射 */
const mapStateToProps = (state) => {
  return {
    str: state.str
  }
}

export default connect(mapStateToProps)(App)
