// import Loading from '@/components/loading';
import Layout from '../components/Layout/layout';
import { lazy } from 'react';
import { createBrowserRouter } from 'react-router-dom';

// const Demo1 = lazy(() => import('../components/FindMusic/index'));
// const Demo2 = lazy(() => import('../components/MyMusic/index'));
// const Demo3 = lazy(() => import('../components/FollowMusic/index'));
import Demo1 from '../components/FindMusic/index';
import Demo2 from '../components/MyMusic/index';
import Demo3 from '../components/FollowMusic/index';


//解决白屏现象
const lazyLoad = (children: ReactNode): ReactNode => {
  return <Suspense fallback={<> loading </>}> {children} </Suspense>
}


const routes = createBrowserRouter([
  {
    path: '/',
    element: <Layout />,
    children: [
      { path: '', element: <Demo1 />, errorElement: '<Loading />' },
      { path: 'find', element: <Demo1 />, errorElement: '<Loading />' },
      { path: 'my', element: <Demo2 />, errorElement: '<Loading />' },
      { path: 'follow', element: <Demo3 />, errorElement: '<Loading />' },
    ],
  },
]);
export default routes;
