import './PlayerBar.less'
import { useEffect, useRef, useState } from 'react';
import { connect, useSelector, useDispatch } from 'react-redux'
import { Slider } from 'antd';
function FootPlay () {
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);

  const audioRef = useRef < HTMLAudioElement > (null);

  const { currentSong } = useSelector((state) => ({/**从redux仓库里面获得数据 */
    currentSong: state.player.currentSong
  }));
  // console.log("currentSong")
  // console.log(currentSong)

  useEffect(() => {
    // audioRef.current.src = getSongPlayUrl(currentSong.id)
    // audioRef.current.src = 'https://lq-sycdn.kuwo.cn/538298535f7e39b6967baa02cd531fdd/660bb72c/resource/n3/46/94/3648821363.mp3'
    // audioRef.current?.play().then(() => {
    //   console.log("播放歌曲失败！")
    // }).catch((err) => {
    //   setIsPlaying(false)
    //   console.log('歌曲播放失败：', err)
    // })
  }, [currentSong])



  function handlePlayBtnClick () {
    isPlaying ? audioRef.current?.pause() : audioRef.current?.play().catch(() => setIsPlaying(false))
    setIsPlaying(!isPlaying)
  }


  function handleTimeUpdate () {//进度条


  }


  return (
    <div className="g-btmbar">
      <div className="m-playbar m-playbar-lock" id="auto-id-MlgyMrpoPkCPw8Sy">
        <div className="updn">
          <div className="left f-fl"><a href="#" className="btn" hidefocus="true" data-action="lock"></a></div>
          <div className="right f-fl"></div>
        </div>
        <div className="bg"></div>
        <div className="hand" title="展开播放条"></div>
        <div className="wrap" id="g_player">
          <div className="btns">
            <a href="#" hidefocus="true" data-action="prev" className="prv" title="上一首(ctrl+←)">上一首</a>
            <a href="#" hidefocus="true" data-action="play" className="ply j-flag" title="播放/暂停(p)">播放/暂停</a>
            <a href="#" hidefocus="true" data-action="next" className="nxt" title="下一首(ctrl+→)">下一首</a>
          </div>
          <div className="head j-flag">
            <img src={currentSong.al?.picUrl}></img>
            <a href="#" hidefocus="true" className="mask"></a>
          </div>
          <div className="play">
            <div className="j-flag words">
              <a hidefocus="true" href="/song?id=2007844922" class="f-thide name fc1 f-fl" title={currentSong?.al?.name}>{currentSong?.al?.name}</a>
              <span class="by f-thide f-fl">
                <span title={currentSong?.name}>
                  <a class="" href="/artist?id=37671780" hidefocus="true">{currentSong?.name}</a>
                </span>
              </span>
            </div>
            <div className="m-pbar" data-action="noop">
              <Slider value={progress} step={0.5} tooltip={{ formatter: null }}>
              </Slider>
              <span className="j-flag time"><em>00:00</em> / 00:00</span>
            </div>
          </div>
          <div className="oper f-fl"><a href="#" hidefocus="true" className="icn icn-pip" title="画中画歌词">画中画歌词</a>
            <a href="#" hidefocus="true" data-action="like" className="icn icn-add j-flag" title="收藏">收藏</a>
            <a href="#" hidefocus="true" data-action="share" className="icn icn-share" title="分享">分享</a>
          </div>
          <div className="ctrl f-fl f-pr j-flag">
            <div className="m-vol" id="auto-id-T14tuTJDkfHf9gKf">
              <div className="barbg"></div>
              <div className="vbg j-t" id="auto-id-1Ek41prB7nTpBCgD"><div className="curr j-t"></div>
                <span className="btn f-alpha j-t"></span></div>
            </div>
            <a href="#" hidefocus="true" data-action="volume" className="icn icn-vol"></a>
            <a href="#" hidefocus="true" data-action="mode" className="icn icn-loop" title="循环"></a>
            <span className="add f-pr">
              <span className="tip">已添加到播放列表</span>
              <a href="#" title="播放列表" hidefocus="true" data-action="panel" className="icn icn-list s-fc3">0</a>
            </span>
            <div className="tip tip-1">循环</div>
          </div>
        </div>
      </div>
      <audio ref={audioRef} onTimeUpdate={handleTimeUpdate}></audio>
    </div>
  )
}
export default FootPlay
