// import Loading from '@/components/loading';
import Layout from '../components/Layout/layout';
import { lazy, ReactNode, Suspense } from "react"
import { createBrowserRouter, Navigate } from 'react-router-dom';

// const Demo1 = lazy(() => import('../components/FindMusic/index'));
// const Demo2 = lazy(() => import('../components/MyMusic/index'));
// const Demo3 = lazy(() => import('../components/FollowMusic/index'));
import Demo1 from '../components/FindMusic/index';
import Demo2 from '../components/MyMusic/index';
import Demo3 from '../components/FollowMusic/index';

//解决白屏现象
const lazyLoad = (children) => {
  return <Suspense fallback={<> loading </>}> {children} </Suspense>
}


const routes = [
  {
    path: '',
    element: <Layout />,
    children: [
      {
        path: '/',
        element: lazyLoad(<Demo1 />)
      },
      {
        path: '/product/card',
        element: lazyLoad(<Demo1 />)
      },
      {
        path: '/pageone/pagefour',
        element: lazyLoad(<Demo1 />)
      }
    ]
  },
  {
    path: '/home',
    element: <Demo1 />
  },
  {
    path: '/pageone',
    element: <Demo1 />
  },
  {
    path: '/login',
    element: <Demo1 />
  },
  {
    path: "*",
    element: <Navigate to="/workbench" />
  }
]

export default routes


